import React from 'react';

import Aux from '../../../hoc/Aux';

import styles from './Modal.module.css';

const Modal = (props) => {
    return (
        <div
            className={styles.Modal}
            style={{
                transform: props.show ? 'translateY(0)' : 'translateY(-100vh)',
                opacity: props.show ? '1' : '0'
            }}>
            {props.children}
        </div>
    );
};

export default Modal;
